import { Component, OnInit } from '@angular/core';
import { Movie } from 'src/app/models/movie';
import { AuthService } from 'src/app/security/auth-service.service';
import { CartService } from 'src/app/services/cart.service';
import { MovieService } from 'src/app/services/movie.service';

@Component({
  selector: 'app-movie-list',
  templateUrl: './movie-list.component.html',
  styleUrls: ['./movie-list.component.css']
})
export class MovieListComponent implements OnInit{

  isLoggedIn?:boolean;

  movies!: Movie[];
  constructor(private authService: AuthService, private movieService: MovieService,
  private cartService: CartService){}



  ngOnInit(): void {
    this.authService.getIsAuthenticated().subscribe({
      next:res => {
        this.isLoggedIn = res.valueOf()}
      });

    this.movieService.getAllMovies().subscribe(data =>{
        this.movies = data;
    })
  }
}
<div class="movie-list text-center background-primary bg-gradient">
  <h3>Available Movies</h3>
  <div class="container flex">
      <div class = "movie-cards" *ngFor="let movie of movies">
          <div class="card">
              <h4>{{movie.title}}</h4><br>
              <img [src] = "movie.imageUri" alt= {{movie.title}}><br>
              <p>Age Rating {{movie.ageRating}}</p>
              <p>Purchase for £{{movie.price}}</p>
              <p>User Rating {{movie.userRating}}</p>
              <div class ="grid">
               <div>
                  <a href="">more info</a>
                  </div>
                  <div *ngIf ="isLoggedIn">
                      <a>add to cart</a>
                  </div>
              </div>
          </div>
      </div>
  </div>
</div>
