import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { Movie } from 'src/app/models/movie';
import { AuthService } from 'src/app/security/auth-service.service';
import { LoadingService } from 'src/app/services/loading.service';
import { MovieService } from 'src/app/services/movie.service';

@Component({
  selector: 'app-add-movie',
  templateUrl: './add-movie.component.html',
  styleUrls: ['./add-movie.component.css']
})
export class AddMovieComponent implements OnInit{

  //Set user logged in value to false
  isLoggedIn: boolean = false;

  //variable to hold loading attribute value
  loading = this.loadingService.loading;

  //declare variable to hold submitted value for registration form
  submitted: boolean = false;

  //Create form group for registration form
  form!: FormGroup;

  //Create user object to hold data collected from new movie form
  private movie: Movie = new Movie();

  //Create cast array to hold cast members of movie
  casts!:String[];
  constructor(private movieService: MovieService,private authService: AuthService,
    private loadingService: LoadingService,private router: Router, private formBuilder: FormBuilder){
}

  onSubmit(){
    this.submitted = true;

    this.movie.title = this.form.get('title')?.getRawValue();
    this.movie.ageRating = this.form.get('ageRating')?.getRawValue();
    this.movie.genre= this.form.get('genre')?.getRawValue();
    this.movie.description = this.form.get('description')?.getRawValue();
    this.movie.cast = this.form.get('cast')?.getRawValue();
    this.movie.price = this.form.get('price')?.getRawValue();
    this.movie.userRating = 0;
    this.movie.imageUri = "./assets/icons/watching-a-movie.png";

    this.movieService.AddNewMovie(this.movie).subscribe({
      next:data => {this.router.navigateByUrl("movie/add");
    }
    });
  }

  addCast(cast: string){
    this.casts.push(cast);

  }

  removeCast(cast: string){
    const index: number = this.casts.indexOf(cast);
    if(index !== -1){
      this.casts.splice(index, 1);
    }

  }
  ngOnInit(): void {
    this.form = this.formBuilder.group({
      title:"",
      ageRating: "",
      genre: "",
      description: "",
      cast:"",
      price: 0,
    })
  }
}
<div class = "new-movie text-center background-primary">
  <div class="container card">
    <form [formGroup]="form" class = "movie-form text-center"(ngSubmit)="onSubmit()">
      <h4>Add New Movie</h4>
      <div class = "form-group">
        <label for="title">Title</label>
        <input type="text" formControlName ="title" class="form-control"
          minlength="2" maxlength="30" id="title" required>
      </div>
      <div class = "form-group">
        <label for="ageRating">Age Rating</label>
        <input type="text" formControlName ="ageRating" class="form-control"
          minlength="2" maxlength="30" id="ageRating" required>
      </div>
      <div class = "form-group">
        <label for="genre">Genre</label>
        <input type="text" formControlName ="genre" class="form-control"
          minlength="2" maxlength="30" id="genre" required>
      </div>
      <div class = "form-group">
        <label for="description">Description</label>
        <textarea type="text" formControlName ="description" class="form-control"
          minlength="10" maxlength="100" id="description" required></textarea>
      </div>
      <div class = "form-group">
        <label for="cast">Cast</label>
        <input #cast (keypress.enter) = "addCast(cast.value)" (blur)="addCast(cast.value); cast.value =''"
          formControlName ="cast" class="form-control"
          minlength="2" maxlength="30" id="cast" required>
          <button type="button" class = "btn" (click)="addCast(cast.value)">Add</button>
          <ul>
            <li *ngFor="let cast of casts">{{cast}}
            <button type="button" class="btn" (click)="removeCast(cast.valueOf())">remove
            </button></li>
          </ul>
      </div>
      <div class = "form-group">
        <label for="price">Price</label>
        <input type="number" formControlName ="price" class="form-control"
          id="price" required>
      </div>
      <app-spinner class="background-primary text-center" *ngIf="loading | async; else elseBlock "></app-spinner>
      <ng-template #elseBlock>
        <button class="btn" type="submit">Add Movie</button>
    </ng-template>
    </form>
  </div>

</div>
