import { Component, OnInit } from '@angular/core';
import { FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { User } from 'src/app/models/user';
import { AuthService } from 'src/app/security/auth-service.service';
import { FormBuilder, Validators } from '@angular/forms';


@Component({
  selector: 'app-registration',
  templateUrl: './registration.component.html',
  styleUrls: ['./registration.component.css']
})
export class RegistrationComponent implements OnInit{

  //Set user logged in value to false
  isLoggedIn: boolean = false;

  //declare variable to hold submitted value for registration form
  submitted: boolean = false;

  //Create form group for registration form
  form!: FormGroup;

  //Create new user object to hold collected registration details
  private user:User = new User();

  constructor(private authService: AuthService, private router: Router,
      private formBuilder: FormBuilder){
  }


  //Create onSubmit function for registration form submition
  onSubmit(){
    this.submitted = true;

    //Create user from form inputs
    this.user.firstname = this.form.get('firstName')?.getRawValue();
    this.user.lastname = this.form.get('lastName')?.getRawValue();
    this.user.username =this.form.get('username')?.getRawValue();
    this.user.password =this.form.get('password')?.getRawValue();
    this.user.roles =["USER"];


    this.authService.register(this.user)
    .subscribe({
        next: data=>{
          localStorage.setItem('username', this.form.get('username')?.getRawValue());
          this.authService.setAuthenticated(data);
          console.log(data);
          this.router.navigateByUrl("userHome");
        }
      }
    );
  }

  ngOnInit(): void {
    this.form = this.formBuilder.group(
      {
        firstName:"",
        lastName:"",
        username:"",
        password:"",
      }
    );
  }
}
<div class="registration backgroung-primary">
  <div class="container grid">
      <form [formGroup]="form" class = "reg-form text-center" (ngSubmit)="onSubmit()">
          <h4>Register</h4>
          <div class="form-group">
              <label for="firstName">First Name</label>
              <input type="text" formControlName="firstName" class="form-control" minlength="2" maxlength="25" id = "firstName" required>
          </div>
          <div class="form-group">
              <label for="lastName">Last Name</label>
              <input type="text"formControlName="lastName"  class="form-control" minlength="2" maxlength="25" id = "lastName" required>
          </div>
          <div class="form-group">
              <label for="username">Username</label>
              <input type="email" formControlName="username" class="form-control" minlength="8" maxlength="20" id = "username" required>
          </div>
          <div class="form-group">
              <label for="password">Password</label>
              <input type="password" formControlName="password" class="form-control" minlength="8" maxlength="20" id = "password" required>
          </div>
          <button type="submit" class="btn">Register</button>
      </form>
      <div class="shop-icon">
          <img src="./assets/icons/sign-up.png" alt="shop">
      </div>
  </div>
</div>
